mapper.select:

select 

from t_system_param
where SYSTEM_PARAM_CATEGORY = #{systemParamCategory,jdbcType=VARCHAR}
  and SYSTEM_PARAM_CODE = #{systemParamCode,jdbcType=VARCHAR}

mapper.delete:

delete from t_system_param
where SYSTEM_PARAM_CATEGORY = #{systemParamCategory,jdbcType=VARCHAR}
  and SYSTEM_PARAM_CODE = #{systemParamCode,jdbcType=VARCHAR}
